import React from 'react';
import './App.css';
import Post from './components/Post/Post';
import styled from 'styled-components'


//Estilização
const ContainerPagina = styled.div `
display: grid;
grid-template-columns: 1fr 3fr;
margin-top: 30px;
justify-content: center;
`
const ContainerPosts = styled.div `
  display: flex;
  justify-content: center;
  flex-direction: column;
  align-items: center;
`
const ContainerForm = styled.div `
display: flex;
flex-direction: column;
`

//Arrays
const listaDePostsInicial = [
  {
    nomeUsuario: "paulinha",
    fotoUsuario: "https://picsum.photos/50/50?a=1",
    fotoPost: "https://picsum.photos/200/150?a=7"
  },
  {
    nomeUsuario: "Dorival_Caymmi",
    fotoUsuario: "https://picsum.photos/50/50?a=2",
    fotoPost: "https://picsum.photos/200/150?a=5"
  },
  {
    nomeUsuario: "Billie Holiday",
    fotoUsuario: "https://picsum.photos/50/50?a=3",
    fotoPost: "https://picsum.photos/200/150?a=6"
  }
]
const listaDePosts = listaDePostsInicial.map((post) => {
  return (
    <Post
    nomeUsuario={post.nomeUsuario }
    fotoUsuario={post.fotoUsuario}
    fotoPost={post.fotoPost}
    />
  )
}
)

//Inputs


function App () {
  state = {    
      valorInputNome: "",
      valorInputfoto: "",
      valorInputimagem: ""  
  }  

  //Funções dos Inputs
  const ParametrosDosInputs = [
{
  nome: "",
  foto: "",
  imagem: ""
}
]

  const nomeUsuarioOnChange = (event) => {
    {this.state.valorInputNome = event.target.value}
  }

  const fotoPerfilOnChange = (event) => {
    {this.state.valorInputFoto = event.target.value}
  }

  const imagemUploadonChange = (event) => {
    {this.state.valorInputImagem = event.target.value}
  }

  const EnviarForm = () => {
    const novoPost = {
      nome: event.target.nome,
      foto: event.target.foto,
      imagem: event.target.imagem
    }

  listaDePosts.push(novoPost)
  console.log(listaDePosts)
  }

  return (
    <ContainerPagina>
      <ContainerForm>
        <h2>Novo Post</h2>
        <input 
        value={ParametrosDosInputs.nome}
        onChange={nomeUsuarioOnChange}
        placeholder={"Nome do Usuário"}
        />
        <input 
        value={ParametrosDosInputs.foto}
        onChange={fotoPerfilOnChange}
        placeholder={"Foto do Perfil"}
        />
        <input 
        value={ParametrosDosInputs.imagem}
        onChange={imagemUploadonChange}
        placeholder={"Imagem para upload"}
        />
        <button onClick={EnviarForm}>
          Enviar!
        </button>        
      </ContainerForm>
  <ContainerPosts>
    {listaDePosts}
  </ContainerPosts>
  </ContainerPagina>
)
}


export default App;
