import React from 'react';
import './App.css';
import Post from './components/Post/Post';
import styled from 'styled-components'

const ContainerPagina = styled.div `
display: flex;
`
const ContainerPosts = styled.div `
  display: flex;
  justify-content: center;
  flex-direction: column;
  align-items: center;
`
const listaDePostsInicial = [
  {
    nomeUsuario: "paulinha",
    fotoUsuario: "https://picsum.photos/50/50?a=1",
    fotoPost: "https://picsum.photos/200/150?a=7"
  },
  {
    nomeUsuario: "Dorival_Caymmi",
    fotoUsuario: "https://picsum.photos/50/50?a=2",
    fotoPost: "https://picsum.photos/200/150?a=5"
  },
  {
    nomeUsuario: "Billie Holiday",
    fotoUsuario: "https://picsum.photos/50/50?a=3",
    fotoPost: "https://picsum.photos/200/150?a=6"
  }
]

const ParametrosDosInputs = [
{
  nomeUsuarioInput: "",
  fotoUsuarioInput: "",
  fotoInput: ""
}
]


const listaDePosts = listaDePostsInicial.map((post) => {
  return (
    <Post
    nomeUsuario={post.nomeUsuario }
    fotoUsuario={post.fotoUsuario}
    fotoPost={post.fotoPost}
    />
  )
}
)

function App () {
  return (
    <ContainerPagina>
      <ContainerForm>
        <input 
        value={nomeUsuarioInput}
        onChange={nomeUsuarioOnchange}
        placeholder={"Nome de Usuário"}
        />
      </ContainerForm>
  <ContainerPosts>
    {listaDePosts}
  </ContainerPosts>
  </ContainerPagina>
)
}


export default App;
