import React from 'react'
import styled from 'styled-components'
import Form1 from './Form1'


const CorpoForm2 = styled.div`
text-align: center;
` 


export class Form2 extends React.Component {
    state = {
        finalizado: true,
    }

    botaoFinalizar = () => {
        this.setState({finalizado: true})
    }
    render () {
        if (this.state.finalizado === true) {
        return <CorpoForm2>
        <p>1. Qual o Curso?</p>
        <input type="text" />
        <p>2. Qual a unidade de ensino?</p>
        <input type="text" />
        <button onClick={this.botaoFinalizar}>Enviar</button>
        </CorpoForm2>
        }
        else {
            return <Form1/>
        }
    }
}

export default Form2
