import React from 'react'
import styled from 'styled-components'
import './styles.css'

const TarefaList = styled.ul`
  padding: 0;
  width: 200px;
`

const Tarefa = styled.li`
  text-align: left;
  text-decoration: ${({completa}) => (completa ? 'line-through' : 'none')};
`

const InputsContainer = styled.div`
  display: grid;
  grid-auto-flow: column;
  gap: 10px;
`

class App extends React.Component {
    state = {
      tarefas: [
        {
          id: Date.now(),
          texto: 'Tarefa exemplo1',
          completa: false,
        },
        {
          id: Date.now(),
          texto: 'Tarefa exemplo2',
          completa: true,
        }
      ],
      inputValue: '',
      filter: ''
    }

  componentDidUpdate() {

  }

  componentDidMount() {

  };

  onChangeInput = (event) => {
    this.setState({inputValue: event.target.value})
  }

  criaTarefa = () => {
        const novaTarefa = {
      id: Date.now(),
      texto: this.state.inputValue,
      completa: false,
    }
    const novaListaDeTarefas = [novaTarefa, ...this.state.tarefas]
  this.setState({
    tarefas: novaListaDeTarefas})
    this.setState({inputValue: ""})
  }

  selectTarefa = (id) => {
  const listaDeTarefas = this.state.tarefas.map((tarefa) => {
    if (id === tarefa.id) {
      const novaTarefa = {
        ...tarefa,
        completa: !tarefa.completa
      }
      return novaTarefa
    } else {
      return tarefa
    }
  })
  this.setState({tarefas: listaDeTarefas});    
  }

  onChangeFilter = (event) => {
    this.setState({filter: event.target.value})
  }

  render() {
    const listaDeTarefas = this.state.tarefas
    localStorage.setItem("tarefa", JSON.stringify(listaDeTarefas))
    const tarefasString = localStorage.getItem("tarefa");
    const tarefasObject = JSON.parse(tarefasString);

    const listaFiltrada = tarefasObject
      .filter(tarefa => {
        switch (this.state.filter) {
          case 'pendentes':
            return !tarefa.completa
          case 'completas':
            return tarefa.completa
          default:
            return true
        }
      })

    return (
      <div className="App">
        <h1>Lista de tarefas</h1>
        <InputsContainer>
          <input value={this.state.inputValue} onChange={this.onChangeInput}/>
          <button onClick={this.criaTarefa}>Adicionar</button>
        </InputsContainer>
        <br/>

        <InputsContainer>
          <label>Filtro</label>
          <select value={this.state.filter} onChange={this.onChangeFilter}>
            <option value="">Nenhum</option>
            <option value="pendentes">Pendentes</option>
            <option value="completas">Completas</option>
          </select>
        </InputsContainer>
        <TarefaList>
          {listaFiltrada.map(tarefa => {
            return (
              <Tarefa
                completa={tarefa.completa}
                onClick={() => this.selectTarefa(tarefa.id)}
              >
                {tarefa.texto}
              </Tarefa>
            ) 
          })}
        </TarefaList>
      </div>
    )
  }
}

export default App
